settings.custom_css:
/* Orbitron laden */
@import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@600;700;800&display=swap');

/* Keine Box / kein Rand / transparenter Hintergrund */
#NovBreadcrumbs.mdw-bc--minimal{
  background: transparent !important;
  border: 0 !important;
  padding: 8px 0 10px;
}

/* Kleines "Startseite" oben */
#NovBreadcrumbs .mdw-bc-home{
  display:block;
  text-align:center;
  font-family:'Orbitron', system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial !important;
  font-size:12px;
  letter-spacing:.02em;
  color: var(--mdw-sub, #9aa3b2);
  text-decoration:none;
  margin:0 0 2px;
}

/* Blog-Titel */
#NovBreadcrumbs .headingBreacrum{
  text-align:center;
  font-family:'Orbitron', system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial !important;
  color: var(--mdw-text, #E9EDF3) !important;
  letter-spacing:.2px;
  line-height:1.15;
  margin:0;
}

/* Optional: Hoverfarbe für "Startseite" */
#NovBreadcrumbs .mdw-bc-home:hover{ color: var(--mdw-brand, #ABCA20); }
