settings.custom_liquid:
{%- if collection -%}
  {%- assign desc_raw = collection.description -%}
  {%- assign desc_plain = desc_raw | strip_html | strip -%}
  {%- if desc_plain != blank -%}
    <section class="collection-description-section">
      <div class="collection-description__container">
        {{ desc_raw }}
      </div>
    </section>

    <style>
      .collection-description-section {
        width: 100%;
        background: #F2F2F2;
        padding: 60px 0;
      }
      .collection-description__container {
        max-width: 1400px;
        margin: 0 auto;
        padding: 0 24px;
        text-align: left;
        color: var(--color-foreground, #333);
        line-height: 1.7;
      }
      .collection-description__container a {
        color: #ABCA20;
        text-decoration: none;
        font-weight: 600;
        transition: color 0.2s ease;
      }
      .collection-description__container a:hover {
        text-decoration: underline;
      }
      @media (max-width: 768px) {
        .collection-description-section {
          padding: 40px 0;
        }
        .collection-description__container {
          padding: 0 20px;
        }
      }
    </style>
  {%- endif -%}
{%- endif -%}
