settings.custom_css:
/* Desktop bleibt 3 Spalten */
.mdw-table{ width:100%; display:grid; gap:10px; margin-top:10px; }
.mdw-trow{ display:grid; grid-template-columns:2fr 2fr 2fr; gap:8px; }
.mdw-thead{ font-weight:700; color:var(--mdw-sub); }
.mdw-trow > div{
  background:rgba(255,255,255,.02);
  border:1px solid var(--mdw-border);
  border-radius:10px; padding:10px;
}

/* --- Mobile-Optimierung: Kartenlayout mit klaren Labels --- */
@media (max-width: 780px){
  .mdw-thead{ display:none; }                 /* Kopf ausblenden */
  .mdw-trow{
    grid-template-columns:1fr;                /* eine Spalte */
    border:1px solid var(--mdw-border);
    border-radius:12px;
    padding:10px;
    background:var(--mdw-card);
  }
  .mdw-trow + .mdw-trow{ margin-top:6px; }

  .mdw-trow .mdw-cell{
    display:grid;
    grid-template-columns:minmax(110px, 36%) 1fr; /* Label | Inhalt */
    gap:10px;
    align-items:start;
    padding:10px 0;
    border-bottom:1px dashed rgba(255,255,255,.08);
    background:transparent; border:0; border-radius:0; /* aus Basis zur√ºcksetzen */
  }
  .mdw-trow .mdw-cell:last-child{ border-bottom:0; padding-bottom:0; }

  .mdw-trow .mdw-cell::before{
    content: attr(data-label);
    font-weight:700;
    color: var(--mdw-sub);
  }
}

/* Optional: etwas mehr Kontrast in Karten */
@media (max-width: 780px){
  .mdw-trow{ box-shadow: 0 8px 20px rgba(0,0,0,.22); }
}

#bereit-fuer-start.mdw-card {
  border: 1px solid rgba(171, 202, 32, 0.55);
  background: rgba(171, 202, 32, 0.05);
  box-shadow: 0 0 12px rgba(171, 202, 32, 0.08);
  transition: all 0.25s ease;
}

#bereit-fuer-start.mdw-card:hover {
  background: rgba(171, 202, 32, 0.08);
  border-color: rgba(171, 202, 32, 0.8);
  box-shadow: 0 0 16px rgba(171, 202, 32, 0.15);
}

/* ===== Einsteiger-Tipps: Buttons in #ABCA20 mit edlem Hover ===== */
.einsteiger-tipps .mdw-btn,
.einsteiger-tipps a.mdw-btn,
.einsteiger-tipps button.mdw-btn,
.einsteiger-tipps .btn {
  --btn-brand: #ABCA20;
  --btn-text: #0E1207;               /* sehr dunkles Gr√ºn/Schwarz f√ºr guten Kontrast */
  background: var(--btn-brand);
  color: var(--btn-text);
  border: 1px solid rgba(0,0,0,.18);
  border-radius: 10px;
  padding: 12px 18px;
  font-weight: 800;
  letter-spacing: .01em;
  text-decoration: none !important;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: .55rem;
  box-shadow:
    0 1px 0 rgba(255,255,255,.12) inset,
    0 8px 18px rgba(171,202,32,.18);
  transition:
    background .18s ease,
    color .18s ease,
    box-shadow .22s ease,
    transform .16s ease;
}

/* Hover: minimal dunkler, leichter Lift & Glow */
.einsteiger-tipps .mdw-btn:hover,
.einsteiger-tipps a.mdw-btn:hover,
.einsteiger-tipps button.mdw-btn:hover,
.einsteiger-tipps .btn:hover {
  background: #9EC114; /* leicht dunkler Ton zu #ABCA20 */
  color: #0A0D05;
  transform: translateY(-1px);
  box-shadow:
    0 2px 0 rgba(255,255,255,.10) inset,
    0 12px 24px rgba(171,202,32,.26),
    0 0 0 1px rgba(171,202,32,.28);
}

/* Active: subtil ‚Äûdr√ºcken‚Äú */
.einsteiger-tipps .mdw-btn:active,
.einsteiger-tipps a.mdw-btn:active,
.einsteiger-tipps button.mdw-btn:active,
.einsteiger-tipps .btn:active {
  transform: translateY(0);
  box-shadow:
    0 1px 0 rgba(255,255,255,.08) inset,
    0 8px 16px rgba(171,202,32,.18);
}

/* Keyboard-Focus: klare, markenkonforme Kontur */
.einsteiger-tipps .mdw-btn:focus-visible,
.einsteiger-tipps a.mdw-btn:focus-visible,
.einsteiger-tipps button.mdw-btn:focus-visible,
.einsteiger-tipps .btn:focus-visible {
  outline: none;
  box-shadow:
    0 0 0 2px #0F1115,
    0 0 0 4px rgba(171,202,32,.75),
    0 10px 24px rgba(171,202,32,.25);
}

/* Disabled-Zustand */
.einsteiger-tipps .mdw-btn[disabled],
.einsteiger-tipps .mdw-btn:disabled {
  opacity: .6;
  filter: grayscale(.1);
  cursor: not-allowed;
  transform: none;
  box-shadow: none;
}

/* Optional: ‚ÄûGhost‚Äú-Buttons in Gr√ºn umrissen */
.einsteiger-tipps .mdw-btn--ghost {
  background: transparent !important;
  color: #ABCA20 !important;
  border-color: rgba(171,202,32,.6) !important;
  box-shadow: none !important;
}
.einsteiger-tipps .mdw-btn--ghost:hover {
  background: rgba(171,202,32,.08) !important;
  color: #ABCA20 !important;
  border-color: rgba(171,202,32,.85) !important;
}
