settings.custom_css:
/* === Fonts: Body = Poppins, Headings = Orbitron (FAQ) === */
.mdw-page.shipping-policy {
  font-family: "Poppins", system-ui, -apple-system, "Segoe UI", Roboto, "Helvetica Neue",
               Arial, "Noto Sans", sans-serif !important;
}
.mdw-page.shipping-policy * { font-family: inherit !important; }

/* Überschriften + Seitentitel + Akkordeon-Summary auf Orbitron */
.mdw-page.shipping-policy h1,
.mdw-page.shipping-policy h2,
.mdw-page.shipping-policy h3,
.mdw-page.shipping-policy h4,
.mdw-page.shipping-policy h5,
.mdw-page.shipping-policy h6,
.mdw-page.shipping-policy .mdw-sidebar-title,
.mdw-page.shipping-policy .mdw-accordion summary {
  font-family: "Orbitron", Arial, sans-serif !important;
  letter-spacing: .02em;
}

/* Feintuning: Akkordeon-Summary etwas kräftiger */
.mdw-page.shipping-policy .mdw-accordion summary {
  font-weight: 700;
}

/* Body-Texte beruhigen */
.mdw-page.shipping-policy p,
.mdw-page.shipping-policy li,
.mdw-page.shipping-policy a,
.mdw-page.shipping-policy .mdw-badge,
.mdw-page.shipping-policy .mdw-acc-body {
  font-weight: 400;
}

/* Fester dunkler Hintergrund, kein globaler Glow */
.mdw-page.shipping-policy {
  background: #17171A !important;
  color: var(--mdw-text);
}

/* Neutrale Token nur für diese Seite überschreiben (weg vom Blau) */
.mdw-page.shipping-policy {
  --mdw-bg: #17171A;
  --mdw-card: #1C1C1F;      /* statt #151922 */
  --mdw-border: #2A2A2E;    /* neutraler Rand */
  --mdw-text: #E9EDF3;
  --mdw-sub: #9aa3b2;
  --mdw-brand: #ABCA20;     /* bleibt gleich (Grün) */
}

/* Karten & Akkordeon-Flächen ohne Blaustich */
.mdw-page.shipping-policy .mdw-card { background: var(--mdw-card) !important; border-color: var(--mdw-border) !important; }
.mdw-page.shipping-policy .mdw-accordion .mdw-acc { background: rgba(255,255,255,.03) !important; border-color: var(--mdw-border) !important; }

/* Sidebar mit dezentem grünem Glow (wie auf Kontakt) */
.mdw-page.shipping-policy .mdw-sidebar{
  background: linear-gradient(180deg, rgba(171,202,32,.06), transparent 70%), var(--mdw-card) !important;
  border: 1px solid var(--mdw-border) !important;
  border-radius: 16px;
  box-shadow:
    0 0 0 1px rgba(171,202,32,.15) inset,
    0 10px 24px rgba(0,0,0,.35),
    0 0 38px rgba(171,202,32,.10);
}
@media (hover:hover){
  .mdw-page.shipping-policy .mdw-sidebar:hover{
    box-shadow:
      0 0 0 1px rgba(171,202,32,.22) inset,
      0 12px 28px rgba(0,0,0,.38),
      0 0 48px rgba(171,202,32,.14);
  }
}
/* Mobile: nicht sticky */
@media (max-width: 900px){
  .mdw-page.shipping-policy .mdw-sidebar{ position: static !important; top: auto !important; }
}

/* Links überall in Marken-Grün (Intro-Link inkl.) */
.mdw-page.shipping-policy .mdw-hero a,
.mdw-page.shipping-policy .mdw-card a,
.mdw-page.shipping-policy .mdw-acc-body a{
  color: var(--mdw-brand) !important;
  text-decoration: underline;
  text-underline-offset: 2px;
  font-weight: 600;
}

/* Buttons/CTAs – neutral statt dunkelblau */
.mdw-page.shipping-policy .mdw-btn--dark{
  background: #232427 !important; /* statt #0f1c2d */
  color: #fff !important;
  box-shadow: 0 1px 0 rgba(255,255,255,.05) inset, 0 6px 14px rgba(0,0,0,.15);
}
.mdw-page.shipping-policy .mdw-btn--dark:hover{ background:#2B2C30 !important; }
.mdw-page.shipping-policy .mdw-btn--dark:active{ background:#232427 !important; }

/* --- Harter Fix gegen Inline-Styles in den FAQ-Blöcken (ersetzt #0f1c2d & Co.) --- */
.mdw-page.shipping-policy .mdw-acc-body [style*="background:#0f1c2d"],
.mdw-page.shipping-policy .mdw-acc-body [style*="background: #0f1c2d"],
.mdw-page.shipping-policy .mdw-acc-body [style*="background-color:#0f1c2d"],
.mdw-page.shipping-policy .mdw-acc-body [style*="background-color: #0f1c2d"]{
  background: #232427 !important;
  color: #fff !important;
}
.mdw-page.shipping-policy .mdw-acc-body a[style*="background:#0f1c2d"],
.mdw-page.shipping-policy .mdw-acc-body a[style*="background: #0f1c2d"]{
  background: #232427 !important;
  color: #fff !important;
}

/* Falls irgendwo noch alte Containerfarben stecken, neutralisieren */
.mdw-page.shipping-policy [style*="background:#0f1115"],
.mdw-page.shipping-policy [style*="background: #0f1115"],
.mdw-page.shipping-policy [style*="background:#151922"],
.mdw-page.shipping-policy [style*="background: #151922"]{
  background: #1C1C1F !important;
}

/* Grüne Info-Boxen: Text schwarz – bereits gesetzt, hier nochmal scoped */
.mdw-page.shipping-policy .mdw-alert--green{
  background:#e8f7db !important;
  border-left:6px solid var(--mdw-brand) !important;
  color:#111 !important;
}
.mdw-page.shipping-policy .mdw-alert--green *,
.mdw-page.shipping-policy .mdw-alert--green .mdw-alert__title{ color:#111 !important; }

/* Auch inline-grüne Boxen (mit inline style) schwarz einfärben */
.mdw-page.shipping-policy .mdw-acc-body [style*="background:#e8f7db"],
.mdw-page.shipping-policy .mdw-acc-body [style*="background: #e8f7db"],
.mdw-page.shipping-policy .mdw-acc-body [style*="background:rgb(232,247,219)"]{
  color:#111 !important;
}
.mdw-page.shipping-policy .mdw-acc-body [style*="background:#e8f7db"] a{
  color: var(--mdw-brand) !important;
}
