settings.custom_css:
#seo-manufacturers .seo-wrap{
  column-count: 1;
  column-gap: 48px;
  column-rule: 0;
  orphans: 3;
  widows: 3;
}

@media (min-width: 992px){
  #seo-manufacturers .seo-wrap{ column-count: 2; }
}

#seo-manufacturers .seo-wrap h2,
#seo-manufacturers .seo-wrap h3,
#seo-manufacturers .seo-wrap p,
#seo-manufacturers .seo-wrap ul,
#seo-manufacturers .seo-wrap ol,
#seo-manufacturers .seo-wrap li{
  break-inside: avoid;
  -webkit-column-break-inside: avoid;
  page-break-inside: avoid;
}

#seo-manufacturers .seo-wrap h2,
#seo-manufacturers .seo-wrap h3{ margin: 0 0 12px; }
#seo-manufacturers .seo-wrap p{ margin: 0 0 14px; }
#seo-manufacturers .seo-wrap ul,
#seo-manufacturers .seo-wrap ol{ margin: 0 0 16px 1.2em; padding: 0; }

#seo-manufacturers .seo-wrap a{
  color: #ABCA20;
  text-decoration: underline;
}

#seo-manufacturers .seo-wrap a:hover,
#seo-manufacturers .seo-wrap a:focus{
  text-decoration: none;
}

#seo-manufacturers .seo-wrap a:focus-visible{
  outline: 2px solid currentColor;
  outline-offset: 2px;
}
