settings.custom_css:
/* === Liquidsteuer: Fonts (Body=Poppins, Headings=Orbitron) === */
.mdw-page.shipping-policy {
  font-family: "Poppins", system-ui, -apple-system, "Segoe UI", Roboto,
               "Helvetica Neue", Arial, "Noto Sans", sans-serif !important;
}
.mdw-page.shipping-policy * { font-family: inherit !important; }

/* √úberschriften + Sidebar-Titel auf Orbitron */
.mdw-page.shipping-policy h1,
.mdw-page.shipping-policy h2,
.mdw-page.shipping-policy h3,
.mdw-page.shipping-policy h4,
.mdw-page.shipping-policy h5,
.mdw-page.shipping-policy h6,
.mdw-page.shipping-policy .mdw-sidebar-title {
  font-family: "Orbitron", Arial, sans-serif !important;
  letter-spacing: .02em;
}

/* Gewicht/Feintuning */
.mdw-page.shipping-policy h1 { font-weight: 800; }
.mdw-page.shipping-policy h2,
.mdw-page.shipping-policy h3 { font-weight: 700; }
.mdw-page.shipping-policy p,
.mdw-page.shipping-policy li,
.mdw-page.shipping-policy a,
.mdw-page.shipping-policy .mdw-badge,
.mdw-page.shipping-policy .mdw-note,
.mdw-page.shipping-policy .mdw-list {
  font-weight: 400;
}


/* Sidebar-Links: neutral, kein Underline / kein Gr√ºn */
.mdw-sidebar a {
  color: var(--mdw-text) !important;
  text-decoration: none !important;
  font-weight: 500 !important;
}
.mdw-sidebar a:hover {
  color: var(--mdw-brand) !important;
  background: rgba(171,202,32,.09);
  border-color: rgba(171,202,32,.25);
}
.mdw-sidebar a[aria-current="page"] {
  background: rgba(171,202,32,.14);
  border-color: rgba(171,202,32,.35);
  box-shadow: inset 0 0 0 1px rgba(171,202,32,.15);
  color: var(--mdw-brand) !important;
}

/* Entfernt "Tipp"-Badge komplett */
.mdw-highlight::after {
  display: none !important;
  content: none !important;
}

/* Highlight-Box beh√§lt gr√ºne Outline */
.mdw-highlight {
  background: var(--mdw-card) !important;
  border: 1px solid rgba(171,202,32,.45) !important;
  box-shadow:
    0 0 0 1px rgba(171,202,32,.25),
    0 0 18px rgba(171,202,32,.08),
    0 8px 22px rgba(0,0,0,.25);
  position: relative;
  transition: box-shadow .25s ease, transform .2s ease;
}
.mdw-highlight:hover {
  transform: translateY(-2px);
  box-shadow:
    0 0 0 1px rgba(171,202,32,.4),
    0 0 28px rgba(171,202,32,.12),
    0 12px 26px rgba(0,0,0,.3);
}
